import React from 'react'

const ShowWord = ({toGuessChars, guessedChars}) => {
    let word = '';
    for(let letter of toGuessChars){
        for(let i=0;i<guessedChars.length;i++){
            if (letter === guessedChars[i])word = word+letter;
            else word = word+'*';
        }
    }
    return (
        <>
          <h2>{word}</h2>  
        </>
    )
}

export default ShowWord
