import ShowDrawing from "./ShowDrawing";
import Button from "./Button";
import ShowWord from "./ShowWord";
import { useState } from 'react'
import Keyboard from "./Keyboard";

const Main = () => {
    let [guessedChars,setGuessedChars]=useState([]);
    let [char,setChar] = useState('');
    let [count,setC] = useState(1);
    function click(){
        if (count >= 10)setC(1);
        else setC(count+1);
      }
    setGuessedChars([guessedChars,char]);
    return (
        <>
            <ShowDrawing drawingIndex={count} />
            <ShowWord toGuessChars={['a','u','t','o']} guessedChars={guessedChars} />
            <Keyboard guessedChars={guessedChars} setChar={setChar}/> 
            <Button name = "Nappi" class_Name = "Nappi" onClick = {click}/> 
        </>
    )
}

export default Main
