import ShowDrawing from "./ShowDrawing";
import ShowWord from "./ShowWord";
import { useState } from 'react'
import Keyboard from "./Keyboard";

const Main = () => {
    function checkGuess(toGuessChars,guessedChars){
        for(let char of toGuessChars){
            if (char === guessedChars[guessedChars.length-1])return true;
        }
        return false;
    }
    let toGuessChars = ['s','a','r','v','i','k','u','o','n','o']
    let [guessedChars,setGuessedChars]=useState([]);
    let [count,setC] = useState(1);
    let flag = true;
    if (guessedChars.length-1>0)flag = checkGuess(toGuessChars,guessedChars);
    if(!flag){
        let temp =count+1
        setC(temp);
    }
    return (
        <>
            <ShowDrawing drawingIndex={count} />
            <ShowWord toGuessChars={toGuessChars} guessedChars={guessedChars} />
            <Keyboard guessedChars={guessedChars} setGuessedChars={setGuessedChars}/>  
        </>
    )
}

export default Main
