import ShowDrawing from "./ShowDrawing";
import Button from "./Button";
import ShowWord from "./ShowWord";
import { useState } from 'react'

const Main = () => {
    let [count,setC] = useState(1);
    function click(){
        if (count >= 10)setC(1);
        else setC(count+1);
      }
    return (
        <>
            <ShowDrawing drawingIndex={count} />
            <ShowWord toGuessChars={['a','u','t','o']} guessedChars={['a','o']} /> 
            <Button name = "Nappi" class_Name = "Nappi" onClick = {click}/> 
        </>
    )
}

export default Main
